// Assuming the parsed data is available in msg.payload
const parsedData = msg.payload;

// Extracting necessary variables from parsed data
const {
    epochTime,
    maxDOAAngle,
    confidenceValue,
    rssiPower,
    channelFrequency,
    antennaArray,
    latency,
    stationID,
    latitude,
    longitude,
    gpsHeading,
    compassHeading,
    mainHeadingSensor
} = parsedData;

const dtD = new Date(epochTime);
const dtD5 = new Date(epochTime + 14400000);

// Default values for latitude and longitude if they are blank
const defaultLatitude = 35.15722;
const defaultLongitude = -79.41477;

// Use default values if latitude and/or longitude are blank
const finalLatitude = latitude !== 0 ? latitude : defaultLatitude;
const finalLongitude = longitude !== 0 ? longitude : defaultLongitude;

// Creating a string with selected variables for the "remarks" field
const selectedVariables = {
    maxDOAAngle,
    confidenceValue,
    rssiPower,
    antennaArray,
    latency,
    stationID,
    gpsHeading,
    compassHeading,
    mainHeadingSensor
};

const remarks = Object.entries(selectedVariables)
    .map(([key, value]) => `${key}: ${JSON.stringify(value)}`)
    .join(", ");

const payload = {
    event: {
        $: {
            version: "2.0",
            type: "b-m-p-s-m",
            uid: `${channelFrequency}`,
            time: dtD.toISOString(),
            start: dtD.toISOString(),
            stale: dtD5.toISOString(),
            how: "h-g-i-g-o"
        },
        point: [
            {
                $: {
                    lat: `${finalLatitude}`,
                    lon: `${finalLongitude}`,
                    hae: "9999999.0",
                    ce: "35.0",
                    le: "9999999.0"
                }
            }
        ],
        detail: {
            status: {
                $: {
                    readiness: "true"
                }
            },
            archive: [{}, {}],
            link: {
                $: {
                    uid: "80085",
                    production_time: dtD.toISOString(),
                    type: "a-f-G-U-C",
                    parent_callsign: "CanaryTAK",
                    relation: "p-p"
                }
            },
            contact: {
                $: {
                    callsign: `Kraken Plot`
                }
            },
            remarks: `${remarks}`,
            color: {
                $: {
                    argb: "-16711681"
                }
            },
            precisionlocation: {
                $: {
                    altsrc: "Kraken"
                }
            },
            usericon: {
                $: {
                    iconsetpath: `COT_MAPPING_SPOTMAP/b-m-p-s-m/-16711681`
                }
            },
            parsedData: parsedData
        }
    }
};

return { payload };

// PORTED FROM KRAKEN SDR's PYTHON Version
// https://github.com/krakenrf/krakensdr_doa/blob/main/_signal_processing/krakenSDR_signal_processor.py#L1289-L1303

var R = 6372.795477598;
var line_length = 100;
var degrees_to_radians = function (degrees) {
    var pi = Math.PI;
    return degrees * (pi / 180);
};
var radians_to_degrees = function (radians) {
    return radians * (180 / Math.PI);
};
var calculateEndLatLng = function (s_lat, s_lng, doa, my_bearing) {
    var theta = degrees_to_radians(my_bearing + (360 - doa)); // that is the relative angle from you the craken to the target 360 degrees minus doa + your bearing) then calculated to radians
    var s_lat_in_rad = degrees_to_radians(s_lat); // starting latitude converted to radians)
    var s_lng_in_rad = degrees_to_radians(s_lng); // starting lng
    var e_lat_in_rad = Math.asin(Math.sin(s_lat_in_rad) * Math.cos(line_length / R) + Math.cos(s_lat_in_rad) * (Math.sin(line_length / R) * Math.cos(theta)));
    var e_lng_in_rad = s_lng_in_rad + Math.atan2(Math.sin(theta) * Math.sin(line_length / R) * Math.cos(s_lat_in_rad), Math.cos(line_length / R) - Math.sin(s_lat_in_rad) * Math.sin(e_lat_in_rad));
    var e_lat = radians_to_degrees(e_lat_in_rad);
    var e_lng = radians_to_degrees(e_lng_in_rad);
    return parseFloat(e_lat.toFixed(6)), parseFloat(e_lng.toFixed(6));
};

var s_lat = msg.payload.latitude
var s_lng = msg.payload.longitude
var doa = msg.payload.maxDOAAngle
var heading = msg.payload.compassHeading

var txPosition = calculateEndLatLng(s_lat,s_lng, doa, heading)

// position of transmitter
// [LAT, LONG]
msg.payload = txPosition;
